var fs = require('fs');
const  path = require('path');
// const dirTree = require('directory-tree');

function MyPlugin() {
}

MyPlugin.prototype.apply = function(compiler) {
  compiler.plugin('emit', function(compilation, callback) {
  	 //get the main directory
  	 var filelist = dirTree('./production/photos', ['.jpg', '.jpeg']);

     filelist = JSON.stringify(filelist);
     filelist = filelist.replace(/.\/production/g , '');
     filelist = JSON.parse(filelist);
  	 //creating and writing objects to main JSON file
  	 fs.writeFile('./production/json/photos.json', JSON.stringify(filelist, null, 4), function(err) {
  	 	 if (err) {
		    return err;
		 }
     console.log('success');
  	 });

     //separate files to each JSON files
     filelist.children.forEach(function(item) {
      fs.writeFile('./production/json/'+ item.name +'.json', JSON.stringify(item, null, 4), function(err) {
       if (err) {
        return err;
        }
       })
    });

    function dirTree(filename) {
    var stats = fs.lstatSync(filename),
        info = {
            path: filename,
            name: path.basename(filename)
        };

    if (stats.isDirectory()) {
        info.type = "folder";
        info.children = fs.readdirSync(filename).map(function(child) {
            return dirTree(filename + '/' + child);
        });
    } else {
        // Assuming it's a file. In real life it could be a symlink or
        // something else!
        info.type = "file";
    }

    return info;
    }

  	 
    
  	 

  	 callback();
  }.bind(this));
};

module.exports = MyPlugin;